// src/index.ts
import path from "path";
import nodecallspython from "node-calls-python";
var py = nodecallspython.interpreter;
var pyFile = path.join(__dirname, "../", "main.py");
async function generateImage(text, options = {
  gridSize: 1
}) {
  const pyModule = await py.import(pyFile);
  const result = await py.call(pyModule, "generate_image", text, options.gridSize);
  return result;
}
var src_default = generateImage;
export {
  src_default as default,
  generateImage
};
